import React, { useEffect, useState } from 'react';

function Main(){
    const [country, setCountry] = useState('');
    const [state, setState] = useState('');
    const [city, setCity] = useState('');
    const [transit, setTransit] = useState('');
    const [countryData, setCountryData] = useState([]);
    const [stateData, setStateData] = useState([]);
    const [cityData, setCityData] = useState([]);

    const handleClick = (e) =>{
        e.preventDefault();

        if((country === "" && state === "" && city === "") || (country !== "" && state !== "" && city !== "")){

        }else{
            if(country !== ""){
                fetch(`http://localhost:3001/filtercountry/${country}`)
                .then(res => res.json())
                    .then(data => {
                        setCountryData(data.response);
                    })
                    .catch(err =>{
                        console.log(err);
                    })
                .catch(err =>{
                    console.log(err);
                });
            }

            if(state !== ""){
                fetch(`http://localhost:3001/filterstate/${state}`)
                .then(res => res.json())
                    .then(data => {
                        setStateData(data.response);
                    })
                    .catch(err =>{
                        console.log(err);
                    })
                .catch(err =>{
                    console.log(err);
                });
            }

            if(city !== ""){
                fetch(`http://localhost:3001/filtercity/${city}`)
                .then(res => res.json())
                    .then(data => {
                        setCityData(data.response);
                    })
                    .catch(err =>{
                        console.log(err);
                    })
                .catch(err =>{
                    console.log(err);
                });
            }
        }
    }

    return(
        <div className="Main">
            <div className="container justify-content-center">
                <form>
                    <div className="form-group mt-5 mb-5">
                        <label for="searchCountry" style={{color: 'white'}}>Search By Country</label>
                        <input id="searchCountry" className="form-control" type="text" onChange={e => setCountry(e.target.value)}></input>
                    </div>

                    <div className="form-group mt-5 mb-5">
                        <label for="searchState" style={{color: 'white'}}>Search By State/Province/Region</label>
                        <input id="searchState" className="form-control" type="text" onChange={e => setState(e.target.value)}></input>
                    </div>

                    <div className="form-group mt-5 mb-5">
                        <label for="searchCity" style={{color: 'white'}}>Search By City/Region</label>
                        <input id="searchCity" className="form-control" type="text" onChange={e => setCity(e.target.value)}></input>
                    </div>

                    <div className="form-group mt-5 mb-5">
                        <label for="searchTransit" style={{color: 'white'}}>Search By City/Region</label>
                        <input id="searchTransit" className="form-control" type="text" onChange={e => setTransit(e.target.value)}></input>
                    </div>
                    <button className="btn btn-light" type="submit" onClick={e => handleClick(e)}>Search</button>
                </form>
            </div>
            <div className="container">

            </div>
        </div>
    )
};

export default Main;
