import React, { useState, useEffect } from 'react';
import './dashboard.css';
import { LineChart, Line } from 'recharts';

function Dashboard(data){
    const [info, setData] = useState(data.data);
    const [graphData, setGraphData] = useState([]);

    useEffect(() =>{
        let cases = info.totalCases;
        let dates = [];
        let graphData = {};

        cases.forEach(x =>{
            if(dates.includes(x.date) === false){
                dates.push(x.date);
            }
        });

        dates.forEach(date =>{
            graphData[date] = 0;
        });

        dates.forEach(date =>{
            cases.forEach(x =>{
                if(x.date === date){
                    graphData[date]++;
                }
            })
        });

        console.log(graphData);

    }, []);

    return(
        <div className="dashboard mt-5 mb-5">
            <div className="container">
                <div className="row mb-5">
                    <div className="col-9">
                        <div className="card bg-transparent" id="card-div">
                            <div className="card-header text-center">
                                <p className="lead" id="card-header">Graphs</p>
                            </div>
                            <div className="card-body">
                                <LineChart width={300} height={300} data={graphData}>
                                    <Line type="monotone" dataKey="cases" stroke="black"></Line>
                                </LineChart>
                            </div>
                        </div>
                    </div>
                    <div className="col-3">
                        <div className="card bg-transparent" id="card-div">
                            <div className="card-header text-center">
                                <p className="lead" id="card-header">Data</p>
                            </div>
                            <div className="card-body">

                            </div>
                        </div>
                    </div>
                </div>
                <div className="row">
                    <div className="col-6">
                        <div className="card bg-transparent" id="card-div">
                            <div className="card-header text-center">
                                <p className="lead" id="card-header">Analysis</p>
                            </div>
                            <div className="card-body">

                            </div>
                        </div>
                    </div>
                    <div className="col-6">
                        <div className="card bg-transparent" id="card-div">
                            <div className="card-header text-center">
                                <p className="lead" id="card-header">Next Steps</p>
                            </div>
                            <div className="card-body">

                            </div>
                        </div>
                    </div>
                </div>
            </div>
        </div>
    )
}

export default Dashboard;
