import React, { useEffect, useState } from 'react';

function Main(){
    const [country, setCountry] = useState('');
    const [state, setState] = useState('');
    const [city, setCity] = useState('');
    const [transit, setTransit] = useState('');
    const [countryData, setCountryData] = useState([]);
    const [stateData, setStateData] = useState([]);
    const [cityData, setCityData] = useState([]);
    const [allData, setAllData] = useState([]);
    const [ontarioCityData, setOntarioCityData] = useState([]);

    useEffect(() =>{    
        fetch("http://localhost:3001/alldata")
        .then(res => res.json())
        .then(data => {
            setAllData(data);
        });
    }, []);

    const handleClick = (e) =>{
        e.preventDefault();

    }

    function renderAlert(message, className){
        const inputDiv = document.getElementById('form-container');
        const inputForm = document.getElementById('search-form');
        const alert = document.createElement('div');
        alert.id = "alert";
        alert.className = "alert center-text mt-5 " + className;
        alert.appendChild(document.createTextNode(message));

        //Check that an alert is not already displayed
        if(document.getElementById("alert")){
            document.getElementById('alert').remove();
            inputDiv.insertBefore(alert, inputForm);
            setTimeout(() =>{
            alert.remove();
            }, 3000);
        }else{
            inputDiv.insertBefore(alert, inputForm);
            setTimeout(() =>{
            alert.remove();
            }, 3000);
        }
    }

    const render = () =>{
        if(countryData.length === 0 || stateData.length === 0 || cityData.length === 0){
            return(
                <div className="container">
                    {
                        allData.map(data =>{
                            return(
                                <p style={{color: 'white'}}>{data.region} - {data.country} - {data.transportation_type} - {data["sub-region"]}</p>
                            )
                        })
                    }
                </div>
            )
        }else{  
            if(countryData.length !== 0){
                return(
                    <div className="container">
                        {
                            countryData.map(data =>{
                                return(
                                    <p style={{color: 'white'}}>{data.region} - {data.country} - {data.transportation_type} - {data["sub-region"]}</p>
                                )                            })
                        }
                    </div>
                )
            }

            if(stateData.length !== 0){
                return(
                    <div className="container">
                        {
                            stateData.map(data =>{
                                return(
                                    <p style={{color: 'white'}}>{data.region} - {data.country} - {data.transportation_type} - {data["sub-region"]}</p>
                                )                            })
                        }
                    </div>
                )
            }

            if(cityData.length !== 0){
                return(
                    <div className="container">
                        {
                            cityData.map(data =>{
                                return(
                                    <p style={{color: 'white'}}>{data.region} - {data.country} - {data.transportation_type} - {data["sub-region"]}</p>
                                )                            })
                        }
                    </div>
                )
            }
        }
    }

    return(
        <div className="Main">
            <div className="container justify-content-center" id="form-container">
                <form id="search-form">
                    <div className="form-group mt-5 mb-5">
                        <label for="searchCountry" style={{color: 'white'}}>Search By Country</label>
                        <input id="searchCountry" className="form-control" type="text" onChange={e => setCountry(e.target.value)}></input>
                    </div>

                    <div className="form-group mt-5 mb-5">
                        <label for="searchState" style={{color: 'white'}}>Search By State/Province/Region</label>
                        <input id="searchState" className="form-control" type="text" onChange={e => setState(e.target.value)}></input>
                    </div>

                    <div className="form-group mt-5 mb-5">
                        <label for="searchCity" style={{color: 'white'}}>Search By City/Region</label>
                        <input id="searchCity" className="form-control" type="text" onChange={e => setCity(e.target.value)}></input>
                    </div>

                    <div className="form-group mt-5 mb-5">
                        <label for="searchTransit" style={{color: 'white'}}>Search By City/Region</label>
                        <input id="searchTransit" className="form-control" type="text" onChange={e => setTransit(e.target.value)}></input>
                    </div>
                    <button className="btn btn-light" type="submit" onClick={e => handleClick(e)}>Search</button>
                </form>
            </div>
            <div className="container">
                {
                    render()
                }
            </div>
        </div>
    )
};

export default Main;
