import React, { useState, useEffect } from 'react';
import './dashboard.css';
import { LineChart, Line, CartesianGrid, XAxis, YAxis } from 'recharts';

function Dashboard(data){
    const [info, setData] = useState(data.data);
    const [graphData, setGraphData] = useState([]);
    const [mobilityData, setMobilityData] = useState([]);


    useEffect(() =>{
        let cases = info.totalCases;
        let dates = [];
        let graphData = {};
        let final = [];

        cases.forEach(x =>{
            if(dates.includes(x.date) === false){
                dates.push(x.date);
            }
        });

        dates.forEach(date =>{
            graphData[date] = 0;
        });

        dates.forEach(date =>{
            cases.forEach(x =>{
                if(x.date === date){
                    graphData[date]++;
                }
            })
        });

        const keys = Object.keys(graphData);

        keys.forEach(key =>{
            final.push({date: key, value: graphData[key]});
        });

        setGraphData(final);

    }, []);


    function total(){
        let total = 0;
        graphData.forEach(data =>{
            total += data.value;
        })

        return total;
    }
    return(
        <div className="dashboard mt-5 mb-5">
            <div className="container">
                <div className="row mb-5">
                    <div className="col-9">
                        <div className="card bg-transparent" id="card-div">
                            <div className="card-header text-center">
                                <p className="lead" id="card-header">Graphs</p>
                            </div>
                            <div className="card-body">
                                <LineChart width={700} height={300} data={graphData}>
                                    <Line type="monotone" dataKey="value" stroke="#F03309"></Line>
                                    <CartesianGrid stroke="#eee" />
                                    <XAxis dataKey="date" stroke="#eee" />
                                    <YAxis dataKey="value" stroke="#eee" />
                                </LineChart>
                                <LineChart width={700} height={300} data={mobilityData}>
                                    <Line type="monotone" dataKey="value" stroke="#F03309"></Line>
                                    <CartesianGrid stroke="#eee" />
                                    <XAxis dataKey="type" stroke="#eee" />
                                    <YAxis dataKey="value" stroke="#eee" />
                                </LineChart>
                            </div>
                        </div>
                    </div>
                    <div className="col-3">
                        <div className="card bg-transparent" id="card-div">
                            <div className="card-header text-center">
                                <p className="lead" id="card-header">Data</p>
                            </div>
                            <div className="card-body">
                                <table className="table">
                                    <thead>
                                        <tr>
                                            <th scope="col">Date</th>
                                            <th scope="col">Cases</th>
                                        </tr>
                                    </thead>
                                    <tbody>
                                        {
                                            graphData.map((data, i) =>{
                                                return(
                                                    <tr key={i} style={{color: 'white'}}>
                                                        <td>{data.date}</td>
                                                        <td>{data.value}</td>
                                                    </tr>
                                                )
                                            })
                                        }
                                        <tr>
                                            <td>Total</td>
                                            <td>{total()}</td>
                                        </tr>
                                    </tbody>
                                </table>
                            </div>
                        </div>
                    </div>
                </div>
                <div className="row">
                    <div className="col-6">
                        <div className="card bg-transparent" id="card-div">
                            <div className="card-header text-center">
                                <p className="lead" id="card-header">Analysis</p>
                            </div>
                            <div className="card-body">

                            </div>
                        </div>
                    </div>
                    <div className="col-6">
                        <div className="card bg-transparent" id="card-div">
                            <div className="card-header text-center">
                                <p className="lead" id="card-header">Next Steps</p>
                            </div>
                            <div className="card-body">

                            </div>
                        </div>
                    </div>
                </div>
            </div>
        </div>
    )
}

export default Dashboard;
